mapper.select:
SELECT * FROM c_work_calendar

	
		${whereSql}
	


	ORDER BY ${orderBySql}


	ORDER BY id DESC

SELECT * FROM c_work_calendar 
WHERE 
day BETWEEN #{startDay,jdbcType=DATE} AND #{endDay,jdbcType=DATE}
AND system="public";

SELECT * FROM c_work_calendar 
WHERE 
day BETWEEN #{startDay,jdbcType=DATE} AND #{endDay,jdbcType=DATE}
AND system=#{system,jdbcType=VARCHAR};

SELECT * from c_work_calendar 
where day BETWEEN #{startDay,jdbcType=DATE} AND #{endDay,jdbcType=DATE} 
AND system = #{system,jdbcType=VARCHAR} 
UNION SELECT * from c_work_calendar WHERE day BETWEEN #{startDay,jdbcType=DATE} AND #{endDay,jdbcType=DATE} 
AND day NOT IN (select day from c_work_calendar where day BETWEEN #{startDay,jdbcType=DATE} AND #{endDay,jdbcType=DATE} AND system = #{system,jdbcType=VARCHAR}) 
ORDER BY day ASC;

		select * 
        from c_work_calendar 
		where day >= #{startDay,jdbcType=DATE} 
		AND system = "public"
		and isWorkDay = "1" 
		ORDER BY day limit #{days,jdbcType=INTEGER},1;

SELECT * FROM c_work_calendar 
WHERE day = #{day,jdbcType=DATE} 
AND system = #{system,jdbcType=VARCHAR}

mapper.update:
UPDATE c_work_calendar 
SET isWorkDay = #{isWorkDay,jdbcType=VARCHAR}, type=CONCAT(type, #{type,jdbcType=VARCHAR}) 
WHERE day BETWEEN #{startDay,jdbcType=DATE} and #{endDay,jdbcType=DATE}
